mapper.select:
SELECT supplier_name
FROM supplier
where supplier_num = #{supplierNum}

mapper.update:
UPDATE purchase_invoice
SET purchase_invoice_SERIAL=#{invoiceSerial}, 
	USER_NUM=#{userNum}, 
	purchase_invoice_DATE=#{invoiceDate}, 
	SUPPLIER_NUM=#{supplierNum}
where purchase_invoice_num = #{invoiceNum}

UPDATE purchase_invoice_item
SET QTY = #{qty}
where purchase_invoice_NUM = #{invoiceNum}
	AND ITEM_NUM = #{itemNum}

mapper.select:
SELECT count(*) cnt FROM purchase_invoice_item
WHERE purchase_invoice_NUM = #{invoiceNum}
	AND ITEM_NUM = #{itemNum}
